mapper.delete:
/* 펀딩 삭제 */
DELETE
FROM
 	each_funding_info 
WHERE 
	funding_code = #{fundingCode};

mapper.select:
/* 진행상태 조회 */
SELECT
	funding_progress AS fundingProgress
FROM
	funding_progress_status;

/* 상품명 조회 */
SELECT
	goods_name AS goodsName
FROM
	goods_reg_info;

/* 상품코드 조회 */
SELECT
	goods_code AS goodsCode
FROM
	goods_reg_info;

/* 재단 목록 이름 조회 */
SELECT			
	fl.foundation_name AS foundationName
FROM 
	foundation_list AS fl
ORDER BY foundation_code;

mapper.update:
/* 펀딩 정보 수정 */ UPDATE each_funding_info
funding_code = #{fundingCode}, funding_name = #{fundingName}, funding_description = #{fundingDescription}, funding_foundation = #{foundationName}, goods_code = #{goodsCode}, funding_goal_amount = #{fundingGoalAmount}, funding_start_date = #{fundingStartDate}, funding_end_date = #{fundingEndDate}, funding_progress = #{fundingProgress}, funding_update_id = #{fundingUpdateId}, funding_update_date = NOW()
WHERE funding_code = #{fundingCode};

mapper.select:
/* 펀딩 목록 조회 */ SELECT f.funding_code AS fundingCode, f.funding_name AS fundingName, f.funding_foundation AS foundationName, f.funding_goal_amount AS fundingGoalAmount, f.funding_start_date AS fundingStartDate, f.funding_end_date AS fundingEndDate, f.funding_progress AS fundingProgress, f.goods_code AS goodsCode, g.goods_name AS goodsName FROM each_funding_info AS f JOIN goods_reg_info AS g ON f.goods_code = g.goods_code
AND ${item.key} LIKE CONCAT('%', #{item.value}, '%')
ORDER BY funding_code DESC;

/* 펀딩 결제 내역 목록 조회 */ SELECT fp.funding_payment_code AS fundingPayCode, fp.user_id AS userId, fp.funding_code AS fundingCode, fp.funding_payment_date AS fundingPaymentDate, fp.funding_payment_amount AS fundingPaymentAmount, pm.pm_name AS pmName FROM funding_payments AS fp JOIN payment_method AS pm ON fp.pm_code = pm.pm_code
${keyword} LIKE CONCAT('%', #{searchValue},'%')
ORDER BY CAST(SUBSTRING_INDEX(funding_payment_code, 'funding_payment',-1) AS unsigned) DESC, fp.funding_payment_code;

/* 진행상태 조회 */
SELECT
	funding_refund_status AS refundStatus
FROM
	funding_pay_refund_status;

mapper.update:
/* 펀딩 환불 상세 정보 수정 */
UPDATE funding_payment_refund

	funding_payment_refund_code = #{fundingRefundCode},
	user_id = #{refundUserId},
	funding_refund_request_amount = #{requestAmount},
	funding_refund_request_reason = #{requestReason},
	funding_refund_status = #{refundStatus}			

WHERE 
	funding_payment_refund_code = #{fundingRefundCode};

mapper.select:
/* 처리상태 환불완료로 업데이트 */ UPDATE funding_payment_refund
funding_refund_status = '환불완료'
WHERE funding_refund_status = '환불전'

/* 펀딩 환불 신청내역 조회 */ SELECT fr.funding_payment_refund_code AS fundingRefundCode, fr.user_id AS refundUserId, fr.funding_payment_code AS fundingPayCode, fr.funding_refund_request_date AS refundRequestDate, fr.funding_refund_request_amount AS requestAmount, fr.funding_refund_bank_name AS refundBank, fr.funding_refund_account AS refundAccount, fr.funding_refund_request_reason AS requestReason, fr.funding_refund_status AS refundStatus, fr.funding_refund_approval_date AS approvalDate, fr.funding_refund_rejection_date AS rejectionDate, fp.funding_payment_date AS fundingPaymentDate, pm.pm_name AS pmName FROM funding_payment_refund AS fr JOIN funding_payments AS fp ON fp.funding_payment_code = fr.funding_payment_code JOIN payment_method AS pm ON pm.pm_code = fp.pm_code
AND ${item.key} LIKE CONCAT('%', #{item.value}, '%')
ORDER BY funding_payment_refund_code DESC;

/* 진행 펀딩 전체 달성률 */
SELECT
	(SUM(funding_current_achieved_amount) / SUM(funding_goal_amount)) * 100 AS allAccomplishmentRate
FROM
	funding_current_amount
WHERE funding_progress = '진행중';

/* 펀딩 모금 진행현황 조회 */ SELECT fc.funding_code AS fundingCode, ef.funding_name AS fundingName, ef.funding_foundation AS foundationName, fc.funding_goal_amount AS fundingGoalAmount, fc.funding_current_achieved_amount AS currentAchievedAmount, fc.funding_achieved_rate AS achievedRate FROM funding_current_amount AS fc LEFT JOIN each_funding_info AS ef ON fc.funding_code = ef.funding_code
${searchKey} LIKE CONCAT('%', #{searchValue},'%')
fc.funding_progress = '진행중';
