mapper.update:
/* 상품 삭제 - 삭제 여부 컬럼 수정, 상품 재고 테이블에 해당 상품의 모든 재고량이 0인 경우에만 삭제 */
UPDATE goods_reg_info
SET
	goods_is_delete = '1'
WHERE
	goods_code = #{goodsCode};

/* 상품 수정 */
UPDATE goods_reg_info

	
		goods_name = #{goodsName}
	
	
		, goods_company = #{goodsCompany}
	
	
		, goods_category = #{goodsCategory}
	
	
		, goods_price = #{goodsPrice}
	
	, goods_update_id = #{goodsUpdId}
	, goods_update_date = NOW()

WHERE
	goods_code = #{goodsCode};

mapper.select:
/* 상품 제조사 조회 */
SELECT
	goods_company AS goodsCompany
FROM
	goods_reg_info;

mapper.insert:
/* 상품 등록 */
INSERT INTO goods_reg_info
	(goods_code, goods_name, goods_company, goods_category, goods_price, goods_reg_id, goods_reg_date)
VALUES
	(sf_new_goods_code(), #{goodsName}, #{goodsCompany}, #{goodsCategory}, #{goodsPrice}, #{goodsRegId}, NOW());

mapper.select:
/* 상품 검색 결과 조회 */ SELECT goods_code , goods_name , goods_company , goods_category , goods_price , goods_reg_id , goods_reg_date , goods_update_id , goods_update_date FROM goods_reg_info WHERE goods_is_delete = '0'
AND ${searchKey} LIKE CONCAT('%', #{searchValue}, '%')
AND ( goods_update_date IS NULL AND goods_reg_date BETWEEN #{startDate} AND #{endDate} OR goods_update_date IS NOT NULL AND goods_update_date BETWEEN #{startDate} AND #{endDate} )
ORDER BY CAST(SUBSTRING_INDEX(goods_code, 'goods', -1) AS UNSIGNED) DESC;

/* 상품 분류 조회 */
SELECT 
	goods_category_code
	, goods_category
FROM
	goods_category_info;
