mapper.delete:
/* 회원 탈퇴 */
DELETE
FROM
	user_info
WHERE 
	user_id = #{userId};

mapper.insert:
/* 회원가입 */
INSERT INTO user_info
(user_id, user_level, user_pw, user_name, user_birth, user_phone, user_email_agree, user_email, user_addr, user_join_date, login_log_code, log_date_calcul, user_update_date, activity_status_standard_code)
VALUES
(#{userId}, #{userLevel}, #{userPw}, #{userName}, #{userBirth}, #{userPhone}, #{userEmailAgree},
 #{userEmail}, #{userAddr}, #{userJoinDate}, #{LoginLogCode}, #{LogDateCalcul}, #{userUpdateDate}, #{activityStatusStandardCode});

mapper.select:
/* 회원 등급 조회 */
SELECT
	 ul.user_level	AS userLevelNum
	,ul.user_level_name	AS userLevelName
FROM
	user_level_standard AS ul;

/* 회원 목록 조회 */ SELECT u.user_id AS userId ,u.user_level AS userLevel ,u.user_pw AS userPw ,u.user_name AS userName ,u.user_birth AS userBirth ,u.user_phone AS userPhone ,u.user_email_agree AS userEmailAgree ,u.user_email AS userEmail ,u.user_addr AS userAddr ,u.user_join_date AS userJoinDate ,u.login_log_code AS loginLogCode ,u.log_date_calcul AS logDateCalcul ,u.user_update_date AS userUpdateDate ,u.activity_status_standard_code AS activityStatusStandardCode FROM user_info AS u
${searchKey} LIKE CONCAT('%', #{searchValue},'%')
ORDER BY u.user_id;
